import { getSafePropertyAccessString } from '@angular/compiler';
import { Component, OnInit } from '@angular/core';
import { Posts } from 'src/app/models/posts.interface';
import { get } from 'src/app/service/posts.service';

@Component({
  selector: 'app-active',
  templateUrl: './active.component.html',
  styleUrls: ['./active.component.scss'],
})
export class ActiveComponent implements OnInit {
  posts!: Posts[] = [];

  constructor() {}

  async ngOnInit() {
    get().then((posts) => {
      this.posts = posts;
      console.log(this.posts);
    });

  }

  onInactivePost(id:number, i:number){
    updatePost({active:false},id)
    this.posts.splice(i,1)
  }
}


<div class="container mt-5">
  <div *ngFor="let post of posts; let i = index">
    <app-post-card *ngIf="post.active" [post]="post">
      <button (click)="onInactivePost(post.id,i)"class="btn btn-primary">Disattiva</button>
    </app-post-card>
  </div>
</div>
