import { Component, OnInit } from '@angular/core';
import { Posts } from 'src/app/models/posts.interface';
import { get, updatePost } from 'src/app/service/posts.service';

@Component({
  selector: 'app-inactive',
  templateUrl: './inactive.component.html',
  styleUrls: ['./inactive.component.scss']
})
export class InactiveComponent implements OnInit {

  posts: Posts[] = [];

  constructor() {
    get().then((posts) => {
      this.posts = posts;
      console.log(this.posts);
    });
  }

  async ngOnInit() {
    const posts = await get()
      this.posts = posts;
      console.log(this.posts);

  }

  onInactivePost(id:number, i:number){
    updatePost({active:false},id)
    this.posts.splice(i,1)
  }
}

<div class="container mt-5">
  <div *ngFor="let post of posts; let i = index">
    <app-post-card *ngIf="!post.active" [post]="post">
      <button (click)="onActivePost(post.id,i)"class="btn btn-primary">Attiva</button>
    </app-post-card>
  </div>
</div>
